public class switchcase {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=3;
		switch (n)
		{
		case 3:
		{
			System.out.println('3');
			break;
		}
		case 5:
		{
			System.out.println('5');
			break;
		}
		case 7:
		{
			System.out.println('7');
			break;
		}
		default:
		{
			System.out.println("default value");
			break;
		}
		}
	}

}
